addEvent("onClientSettingChange", true)addEventHandler("onClientSettingChange", localPlayer,function ()	local setting = exports.GTIsettings:getSetting("detailed_radar")		if setting == "Yes" then			exports.GTIdetailedRadar:enableDetailedRadar(true)		else			exports.GTIdetailedRadar:enableDetailedRadar(false)		endend)addEventHandler("onClientResourceStart", resourceRoot,	function()		local setting = exports.GTIsettings:getSetting("detailed_radar")			if setting == "Yes" then				exports.GTIdetailedRadar:enableDetailedRadar(false)			end	end)addEventHandler( "onClientResourceStop", getResourceRootElement( getThisResource()), 	function ()		exports.GTIdetailedRadar:enableDetailedRadar(false)	end)