local localPlayer = getLocalPlayer()local theFont = dxCreateFont( "DGI.ttf", 50 )local sWidth,sHeight = guiGetScreenSize()local rWidth,rHeight = 1600,900local SAFEZONE_X = sWidth*0.05local SAFEZONE_Y = sHeight*0.05local sW,sH =  (sWidth/rWidth), (sHeight/rHeight)local alphaI = {[1] = 255, [2] = 0}local alpha = 1function math.round(number, decimals, method)    decimals = decimals or 0    local factor = 10 ^ decimals    if (method == "ceil" or method == "floor") then return math[method](number * factor) / factor    else return tonumber(("%."..decimals.."f"):format(number)) endendaddEventHandler("onClientRender",root,    function()    if not isPedInVehicle(localPlayer) then return end		local vehicle = getPedOccupiedVehicle( localPlayer )		if (not isElement(vehicle)) then return end		local vHP = getElementHealth ( vehicle )		local kph = exports.GTIutil:getElementSpeed(vehicle, "kph")		if vHP and (vHP > 0) then        local hp = (vHP-250)/750        local r = 255*(1 - hp)	if r > 255 then r = 255 end        local g = 255*hp	if g > 50 then g = 255 end	if g < 0 then g = 0 end		dxDrawRectangle(sW*1472, sH*747, sW*80, sH*12, tocolor(228, 120, 24, 120), false)        dxDrawRectangle(sW*1472, sH*765, sW*80,sH*12, tocolor(r, g, 0, 120), false)        dxDrawRectangle(sW*1472, sH*765, sW*80/1000*vHP,sH*12, tocolor(r, g, 0, 255), false)        dxDrawText((math.floor(vHP/10)).."% HP", sW*1488, sH*764, sW*1537, sH*775, tocolor(3, 0, 0, 255), sH*0.9, "sans", "center", "top",false, false, false, false, false)        dxDrawText("km/h", sW*1468, sH*779, sW*1572, sH*800, tocolor(255, 255, 255, 255), 2, "default", "left", "top", false, false, false, false, false)        dxDrawText(""..math.floor(kph).."", sW*1500+dxGetTextWidth(kph), sH*731, sW*1458, sH*855, tocolor(255, 255, 255, 255), 1, theFont, "right", "top", false, false, false, false, false)        if ( isVehicleLocked (vehicle) == true ) then	    	dxDrawImage(sW*1519, sH*710, sW*40, sH*35, ":GTIvehicleHUD/lock_closed.png", 0, 0, 0, tocolor(255, 255, 255, 255), false)	    else		    dxDrawImage(sW*1519, sH*710, sW*40, sH*35, ":GTIvehicleHUD/lock_open.png", 0, 0, 0, tocolor(255, 255, 255, 255), false)        end		if sWidth >= 480 and sWidth < 600 then			textSizeA, textSizeB = .50, 1.00			sizeA, sizeB = 18, 24			size = 1.020		elseif sWidth >= 600 and sWidth < 720 then			textSizeA, textSizeB = 0.75, 1.50			sizeA, sizeB = 26, 32			size = 1.27		elseif sWidth >= 720 and sWidth < 900 then			textSizeA, textSizeB = 0.75, 1.50			sizeA, sizeB = 26, 32			size = 2.05		elseif sWidth >= 900 then			textSizeA, textSizeB = 1.00, 1.50			sizeA, sizeB = 26, 32			size = 2.54		end		if getVehicleType(vehicle) == "Plane" then			local x, y, z = getElementPosition( vehicle)			local rX, rY, rZ = getElementRotation( vehicle)			local state = isVehicleOnGround( vehicle)			if state then				local blinkSeconds = getTickCount() / 2000				local blinks = math.deg(blinkSeconds) * 5				gA = blinks			else				gA = 255			end						if not state then				if rX >= 180 then					gR, gG, gB = 123, 45, 49				elseif rX < 40 then					gR, gG, gB = 49, 123, 45				end			else				gR, gG, gB = 255, 255, 255			end			dxDrawImage(1431, 642, 141, 33, "gimbal.png", rY, 0, 0, tocolor(gR, gG, gB, gA), true)			end		if getVehicleType(vehicle) == "BMX" then return end			local fuel = getElementData(vehicle,"fuel")	if not fuel then return false end		local fuel = math.floor(fuel)	if fuel >= 50 then			    rf, gf, bf = 0, 0, 0	    elseif fuel < 50 then			    rf, gf, bf = 255, 255, 0	    elseif fuel < 30 then			    rf, gf, bf = 255, 10, 0	end		dxDrawRectangle(sW*1472, sH*747, sW*(fuel*0.8), sH*12, tocolor(227, 121, 27, 255), false)        dxDrawText("".. tostring(fuel) .." Litres", 1491, 746, 1532, 758, tocolor(0,0,0,alphaI[alpha]), 0.9, "sans", "center", "top", false, false, true, false, false)        end    end)alphaTimer = setTimer(function ()	if not isPedInVehicle(localPlayer) then return end	local veh = getPedOccupiedVehicle(localPlayer)	if (not veh) then return end	if (getVehicleController(veh) ~= localPlayer) then return end	local fuel = tonumber(getElementData(veh,"fuel"))	if (not fuel) then return end	local fuel = math.floor(fuel)	if (fuel < 40 and fuel > 0) then 		if alpha >= 2 then alpha = 0 end 		alpha = alpha +1 	else 		alpha = 1	endend,150,0) addEventHandler("onClientResourceStart",resourceRoot,function ()	fileDelete("vehicle_hud.lua")end )